import React from 'react'

const Hero = () => {
  return (
    <div
      name="home"
      style={{
        backgroundImage: `url("https://source.unsplash.com/1600x900/?party")`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "100%",
        height: "100vh",
      }}
      className="w-full h-screen flex flex-col justify-between sm:bg-contain"
      id="home"
    >
      <div className="grid md:grid-cols-2 max-w-[1240px] m-auto">
        <div className="flex flex-col justify-center md:items-start w-full px-2 py-8">
          <p className="text-4xl md:text-5xl lg:text-6xl text-cyan-950 shadow-3xl">
            Everything you need whilst travelling
          </p>
          <h1 className="py-3 text-4xl md:text-5xl lg:text-7xl font-bold text-cyan-950">
            Events, Concerts, Restaurants
          </h1>
          <p className="text-2xl md:text-3xl lg:text-4xl text-cyan-950 drop-shadow-2xl">
            And so much more
          </p>
        </div>
      </div>
      <style>{`
        @media only screen and (max-width: 768px) {
            #home {
            background-size: 10%;
          }
            .text-cyan-950 {
            font-size: 3rem;
          }
            .text-4xl {
            font-size: 2.5rem;
          }
            .text-2xl {
            font-size: 1.5rem;
          }
        }
          @media (max-width: 640px) {
    #home {
      background-size: 0% !important;
    }
  }

      `}</style>
    </div>
  );
};

export default Hero
