function Faves({
  title,
  date,
  address,
  link,
  image,
  description,
  event_location_map,
  setSavedCards,
  cards = [],
}) {
  const id = `${title}-${date}-${address}`; // concatenate the id

  const [isFave, setIsFave] = useState(
    () => localStorage.getItem(id) === "true"
  );

  const handleFaveClick = () => {
    const newIsFave = !isFave;
    setIsFave(newIsFave);
    if (newIsFave) {
      const card = {
        id,
        title,
        date,
        event_location_map,
        address,
        link,
        image,
        description,
      };
      onAdd(card);
    } else {
      handleRemoveFaveCard(id);
    }
  };

  const onAdd = (card) => {
    const faveCards = JSON.parse(localStorage.getItem("faves") || "[]");
    const newFaveCards = [...faveCards, card];
    localStorage.setItem("faves", JSON.stringify(newFaveCards));
    localStorage.setItem(id, "true");
  };

  const handleRemoveFaveCard = (id) => {
    const faveCards = JSON.parse(localStorage.getItem("faves") || "[]");
    const newFaveCards = faveCards.filter((card) => card.id !== id);
    setSavedCards(newFaveCards);
    localStorage.setItem("faves", JSON.stringify(newFaveCards));
    localStorage.removeItem(id);
  };

  return (
    <>
      {isFave || cards.some((card) => card.id === id) ? (
        <button onClick={handleFaveClick} style={{ border: "none" }}>
          <CiBeerMugFull className="text-yellow-600 h-10 w-10" />
        </button>
      ) : (
        <button onClick={handleFaveClick} style={{ border: "none" }}>
          <CiBeerMugFull className="text-black lg:text-black border-cyan-950 h-10 w-10" />
        </button>
      )}
    </>
  );
}

export default Faves;
