import React, { useState } from "react";
import emailjs from "emailjs-com";

function ContactUs() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Send email using EmailJS
    const serviceID = "service_cibo1ek";
    const templateID = "template_gwkv6pb";
    const userID = "RrKeOKOTJdH1gBlTs";

    emailjs.send(serviceID, templateID, formData, userID).then(
      (response) => {
        setFormData({
          name: "",
          email: "",
          message: "",
        });
      },
    );
  };

  return (
    <div className="flex flex-col items-center justify-center">
      <h2 className="text-3xl font-bold mb-8 text-white my-6" id="contact-us">
        EVENT QUERIES
      </h2>
      <form onSubmit={handleSubmit} className="w-10/12 max-w-md">
        <div className="mb-4">
          <input
            name="name"
            value={formData.name}
            onChange={handleChange}
            type="text"
            placeholder="Your Name"
            className="shadow border rounded-2xl w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline caret-pink-500"
          />
        </div>
        <div className="mb-4">
          <input
            name="email"
            value={formData.email}
            onChange={handleChange}
            type="email"
            placeholder="Your Email"
            className="shadow appearance-none border rounded-2xl w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-4">
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            placeholder="Your Message to Us.."
            cols="22"
            rows="7"
            className="shadow rounded-2xl w-full py-2 px-3 text-black leading-tight caret-pink-500"
          ></textarea>
        </div>
        <div className="flex justify-center">
          <button
            type="submit"
            className="bg-cyan-900 outline hover:bg-cyan-950 hover:text-white text-white font-bold py-2 px-4 rounded-3xl focus:outline-none focus:shadow-outline"
          >
            Submit Message
          </button>
        </div>
      </form>
    </div>
  );
}

export default ContactUs;
