import React from 'react'

const Hero = () => {
    return (
        <div
        name="home"
        style={{
            backgroundImage: `url("https://source.unsplash.com/1600x900/?party")`,
            backgroundRepeat: "no-repeat",
            backgroundSize: "100%",
            height: "100vh",
        }}
        className="w-full h-screen flex flex-col justify-between"
        id="home"
        >
        <div className="intro grid md:grid-cols-2 max-w-[1240px] m-auto">
            <div className="flex flex-col justify-center md:items-start w-full px-2 py-8">
            <h1 className='text-5xl text-cyan-950 px-6 outline-4' style={{textShadow: "1px 1px 2px rgba(0,0,0,0.5)"}}>
                Everything you need whilst travelling
            </h1>
            <br></br>
            <h2 className='text-5xl text-cyan-950 px-6' style={{textShadow: "1px 1px 2px rgba(0,0,0,0.5)"}}>
                <i>Food Events, Concerts, Raves!</i>
            </h2>
            <br></br>
            <h2 className='text-5xl text-cyan-950 px-6' style={{textShadow: "1px 1px 2px rgba(0,0,0,0.5)"}}>
                And so much more!
            </h2>
            </div>
        </div>
        <style>{`
            @media only screen and (max-width: 768px) {
            #home {
                background-size: contain !important;
            }
            .intro {
                margin: 20px;
            }
            .text-cyan-950 {
                font-size: 3rem;
            }
            .text-4xl {
                font-size: 2.5rem;
            }
            .text-2xl {
                font-size: 1.5rem;
            }
            }
        `}</style>
        </div>
    );
    };

export default Hero
