import React from "react";
import Faves from "../faves/faves";

function Card({
  title,
  date,
  address,
  link,
  image,
  description,
  event_location_map,
  id,
}) {
  return (
    <main className="py-6 px-4 sm:p-6 md:py-10 md:px-8 h-">
      <div className="max-w-4xl mx-auto grid grid-cols-1 lg:max-w-5xl lg:gap-x-20 lg:grid-cols-2 bg-white rounded-2xl">
        <div className="relative p-3 col-start-1 row-start-1 flex flex-col-reverse bg-gradient-to-t from-black/75 via-black/0 sm:bg-none sm:row-start-2 sm:p-0 lg:row-start-1 overflow-hidden">
          <div className="flex justify-between">
            <h2 className="m-1 sm:m-3 pt-2 text-lg font-semibold text-white sm:text-slate-900 md:text-2xl dark:sm:text-white">
              {title}
            </h2>
            <div className="m-1 sm:m-3">
              <Faves
                id={id}
                title={title}
                date={date}
                address={address}
                link={link}
                image={image}
                description={description}
                event_location_map={event_location_map}
              />
            </div>
          </div>
        </div>
        <div className="grid gap-4 col-start-1 col-end-3 row-start-1 sm:mb-6 sm:grid-cols-4 lg:gap-6 lg:col-start-2 lg:row-end-6 lg:row-span-6 lg:mb-0">
          <img
            src={image}
            alt={title}
            className="w-full h-52 object-cover rounded-lg sm:h-full sm:col-span-2 lg:col-span-full"
            loading="lazy"
          />
        </div>
        <dl className="mt-4 text-xs font-medium flex items-center row-start-2 sm:mt-1 sm:row-start-3 md:mt-2.5 lg:row-start-2">
          <dt className="sr-only">Location</dt>
          <dd className="flex items-center">
            {event_location_map && (
              <>
                <a
                  href={event_location_map.link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <svg
                    width="24"
                    height="24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    className="mr-1 text-slate-400 dark:text-slate-500"
                    aria-hidden="true"
                  >
                    <path d="M18 11.034C18 14.897 12 19 12 19s-6-4.103-6-7.966C6 7.655 8.819 5 12 5s6 2.655 6 6.034Z" />
                    <path d="M14 11a2 2 0 1 1-4 0 2 2 0 0 1 4 0Z" />
                  </svg>
                  {address && <dd>{address.join(", ")}</dd>}
                </a>
                {date && <dd>{date.when}</dd>}
              </>
            )}
          </dd>
        </dl>
        <div class="mt-4 col-start-1 row-start-3 self-center sm:mt-0 sm:col-start-2 sm:row-start-2 sm:row-span-2 lg:mt-6 lg:col-start-1 lg:row-start-3 lg:row-end-4">
          <button
            type="button"
            class="bg-indigo-600 text-white text-sm leading-6 font-medium py-2 px-3 rounded-lg"
          >
            Check availability
          </button>
        </div>
        <p class="mt-4 text-sm leading-6 col-start-1 sm:col-span-2 lg:mt-6 lg:row-start-4 lg:col-span-1 dark:text-slate-400">
          {description}
        </p>
      </div>
    </main>
  );
}

export default Card;
